CREATE DEFINER=`root`@`localhost` TRIGGER `employee_BEFORE_INSERT` BEFORE INSERT ON `employee` FOR EACH ROW BEGIN INSERT INTO employeeregaudit(id) VALUES (NULL); IF (NEW.position = 'Manager') THEN SET NEW.CharID = CONCAT('MG', LPAD(LAST_INSERT_ID(), 3, '0')); ELSEIF (NEW.position = 'Admin') THEN SET NEW.CharID = CONCAT('AD', LPAD(LAST_INSERT_ID(), 3, '0')); ELSE SET NEW.CharID = CONCAT('EMP', LPAD(LAST_INSERT_ID(), 3, '0')); END IF; END
CREATE DEFINER=`root`@`localhost` TRIGGER `employee_AFTER_INSERT_1` AFTER INSERT ON `employee` FOR EACH ROW BEGIN INSERT INTO userlogin VALUES(NEW.CharID, NEW.password); END
CREATE DEFINER=`root`@`localhost` TRIGGER `employee_BEFORE_UPDATE` BEFORE UPDATE ON `employee` FOR EACH ROW BEGIN UPDATE userlogin SET password = NEW.password WHERE userId = NEW.CharID; END
CREATE DEFINER=`root`@`localhost` TRIGGER `employee_BEFORE_DELETE` BEFORE DELETE ON `employee` FOR EACH ROW BEGIN DELETE FROM userlogin WHERE userId = OLD.CharID; END
